INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (37, '天一阁', '天一阁位于宁波海曙，由明代兵部右侍郎范钦主持建造，占地2.6万平方米，已有400多年历史。', '明代中期', '中国现存最早的私家藏书楼，亚洲现有最古老的图书馆。', '/images/a80bb57c-50c2-42c4-ac74-7aa0e1f97d1b.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (38, '十里红妆', '十里红妆是旧时宁海地区特有的婚俗，发源于宁海，流传于宁海、象山、三门等地。常用“良田千亩，十里红妆”形容嫁妆的丰厚。', '明清时期', '国家级非物质文化遗产，展现了浙东地区的传统婚嫁习俗。', '/images/81417da5-9722-45ed-a06f-ce082db27944.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (39, '宁波走书', '宁波走书是浙江省宁波市的地方传统曲艺剧种之一，流行于鄞县、奉化、镇海、定海等地，表演形式丰富。', '清代光绪年间', '具有浓郁的地方特色，是宁波民间艺术的瑰宝。', '/images/1e501214-ffb4-4096-9def-98b2b7bb8002.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (40, '河姆渡遗址', '河姆渡遗址位于宁波余姚，是中国长江流域下游以南地区发现的最早的新石器时代遗址之一。', '新石器时代', '证明了长江流域也是中华文明的重要发源地之一。', '/images/7ef7fb46-c448-4425-9f67-085ed37da2a9.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (41, '梁祝传说', '梁山伯与祝英台传说是我国四大民间传说之一，宁波可以说是梁祝故事的发源地，鄞州区建有梁祝文化公园。', '东晋时期', '中国最具影响力的口头民间传说之一，被誉为“东方的罗密欧与朱丽叶”。', '/images/bce84cff-4c69-4626-b76a-78f4fda33fe5.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (42, '保国寺', '保国寺位于宁波江北区，其大殿是江南最古老、保存最完整的木结构建筑之一，结构奇特，气势雄伟。', '北宋', '江南地区最古老、保存最完整的木结构建筑，建筑学价值极高。', '/images/ee994b39-e4b3-447b-b9d0-39377e77e3f7.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (43, '宁波帮', '宁波帮是中国近代最大的商帮，对中国近代工商业的发展做出了巨大的贡献，包玉刚、邵逸夫等都是著名的宁波帮代表人物。', '近现代', '中国十大商帮之一，对中国近代工商业和金融业发展影响深远。', '/images/7065371f-3b4a-4a71-9891-b0ae54dc9905.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (44, '慈城古县城', '慈城是江南地区唯一保存较为完整的古县城，保留了唐宋明清各朝代的建筑风格，被誉为“江南第一古县城”。', '唐宋明清', '中国慈孝文化之乡，保存了完整的古县城格局。', '/images/196c4d62-d921-41a3-9e43-97bcc90898df.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (45, '四明南词', '四明南词是流行于宁波地区的一种传统曲艺形式，曲调优雅，唱腔婉转，伴奏乐器独特。', '明末清初', '国家级非物质文化遗产，宁波地区特有的曲艺形式。', '/images/186e56e2-ef92-41e8-ba59-57105ba83363.jpg');
INSERT INTO ningbo.ningbo_culture (id, culture_title, content, history_period, significance, culture_image) VALUES (46, '骨木镶嵌', '宁波骨木镶嵌是国家级非物质文化遗产，利用牛骨、木片等材料在木器上镶嵌出各种图案，工艺精湛。', '隋唐时期', '宁波传统工艺美术的代表，具有极高的艺术收藏价值。', '/images/215ccefa-7a2d-435b-8e2f-0206673a9ea0.jpg');
