INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (49, '东钱湖', '浙江省最大的天然淡水湖，面积为杭州西湖的3倍，湖光山色，风景秀丽，适合骑行和露营。', '宁波市鄞州区东钱湖镇', '免费', '全天开放', '/images/9aa0ffc2-b4d4-4e26-9165-5e4c37765e1e.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (50, '溪口-雪窦山', '国家5A级景区，被誉为“四明第一山”，是中国五大佛教名山之一的弥勒佛道场，拥有千丈岩瀑布。', '宁波市奉化区溪口镇', '150元', '08:00-16:30', '/images/f69731aa-4099-49c8-8f62-e3d4bf05611f.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (51, '老外滩', '于1844年开埠，保留了大量欧式建筑，是宁波时尚与历史的交汇点，夜景尤其迷人。', '宁波市江北区中马路', '免费', '全天开放', '/images/fe0a1f5d-988c-4394-bdc2-72cb95cd721e.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (52, '月湖公园', '宁波市中心的城市湖泊，历史悠久，周边分布着大量的名人故居和博物馆，如天一阁就在旁。', '宁波市海曙区县学街', '免费', '全天开放', '/images/f05c92f9-e4ff-421f-b7b4-24c5edc1ed73.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (53, '招宝山', '位于甬江出海口，是镇海关隘要塞，集海天风光、人文景观、海防遗址、佛教文化于一体。', '宁波市镇海区招宝山路', '60元', '08:00-17:00', '/images/b465931c-b6e6-4f5c-8c3d-7474f98c4c6d.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (54, '九龙湖', '九龙湖风景区群山环抱，湖水清澈，环境幽静，是休闲度假、烧烤露营的好去处。', '宁波市镇海区九龙湖镇', '30元', '08:00-16:30', '/images/382e7a60-40c5-4654-92e1-8c3315422216.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (55, '前童古镇', '一座不一样的江南古镇，家家有雕梁，户户有活水，八卦水系流遍全村，保留了完整的明清建筑群。', '宁波市宁海县前童镇', '70元', '08:00-17:00', '/images/04bb84ad-cfe9-468f-87d0-dbf1d1e62689.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (56, '石浦渔港古城', '中国四大渔港之一，古城沿山而筑，依山临海，充满了浓郁的渔家风情和海防文化。', '宁波市象山县石浦镇', '60元', '08:00-17:00', '/images/96b783ca-f13f-460b-acab-2b310511650e.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (57, '天童寺', '号称“东南佛国”，寺院依山而建，气势恢宏，拥有极其珍贵的古树群，是佛教禅宗五大名刹之一。', '宁波市鄞州区东吴镇', '免费', '06:00-16:00', '/images/30a1ea12-70ff-4327-a47b-548eac90dbcb.jpg');
INSERT INTO ningbo.ningbo_scenery (id, scenery_name, description, address, ticket_price, open_hours, scenery_image) VALUES (58, '南塘老街', '集历史古迹、旅游观光、文化休闲、宁波老字号美食于一体，是感受宁波市井生活的好地方。', '宁波市海曙区船埠巷', '免费', '全天开放', '/images/1578e246-0aa9-456d-8aff-39197de76672.jpg');
